mapper.select:
SELECT p.prod_sn, p.prod_nm, p.prod_instl_amt_min, p.prod_instl_amt_max, p.prod_cd, p.prod_ty_cd, p.prod_pay_ty_cd, p.prod_int_tax_ty_cd, p.prod_air_min, p.prod_air_max, p.prod_ntsl_bgn_ymd, p.prod_ntsl_end_ymd, p.prod_curr_stcd FROM tb_product p WHERE 1=1
AND p.prod_ty_cd = #{prodTyCd}
AND p.prod_curr_stcd = #{prodCurrStcd}
AND p.prod_pay_ty_cd = #{prodPayTyCd}
AND p.prod_nm LIKE #{prodNmPattern}
AND p.prod_ntsl_bgn_ymd BETWEEN #{prodNtslBgnYmdRange[0]} AND #{prodNtslBgnYmdRange[1]}
AND p.prod_ntsl_end_ymd BETWEEN #{prodNtslEndYmdRange[0]} AND #{prodNtslEndYmdRange[1]}

SELECT CASE WHEN s.dsgn_ds_ty_cd = '1' THEN '적금' WHEN s.dsgn_ds_ty_cd = '2' THEN '목돈' WHEN s.dsgn_ds_ty_cd = '3' THEN '예금' WHEN s.dsgn_ds_ty_cd = '4' THEN '대출' END AS dsgn_ds_ty, COUNT(s.dsgn_sn) AS total_design_count, SUM( CASE WHEN s.dsgn_ds_ty_cd = '1' THEN savg.savg_tot_dpst_amt WHEN s.dsgn_ds_ty_cd = '2' THEN acml.acml_tot_dpst_amt WHEN s.dsgn_ds_ty_cd = '3' THEN d.dpst_amt WHEN s.dsgn_ds_ty_cd = '4' THEN loan.loan_amt END ) AS total_sales_amount FROM tb_custprod_dsgn s LEFT JOIN tb_prod_dsgn_savg savg ON s.dsgn_sn = savg.dsgn_sn AND s.dsgn_ds_ty_cd = '1' LEFT JOIN tb_prod_dsgn_acml acml ON s.dsgn_sn = acml.dsgn_sn AND s.dsgn_ds_ty_cd = '2' LEFT JOIN tb_prod_dsgn_dpst d ON s.dsgn_sn = d.dsgn_sn AND s.dsgn_ds_ty_cd = '3' LEFT JOIN tb_prod_dsgn_loan loan ON s.dsgn_sn = loan.dsgn_sn AND s.dsgn_ds_ty_cd = '4' LEFT JOIN tb_product p ON s.prod_sn = p.prod_sn WHERE s.dsgn_create_at BETWEEN #{startOfMonth} AND #{endOfMonth}
AND s.user_id = #{userId}
AND s.dsgn_ds_ty_cd = #{dsgnDsTy}
AND p.prod_nm LIKE CONCAT('%', #{prodNm}, '%')
GROUP BY s.dsgn_ds_ty_cd ORDER BY total_sales_amount DESC;
