mapper.select:
SELECT p.prod_sn, p.prod_nm, p.prod_instl_amt_min, p.prod_instl_amt_max, p.prod_create_at, p.prod_update_at, p.user_id, p.prod_cd, c1.code_nm AS prodTyCd, c2.code_nm AS prodSbstgTyCd, c3.code_nm AS prodPayTyCd, c4.code_nm AS prodIntTaxTyCd, p.prod_air_min, p.prod_air_max, p.prod_air_bgn_ymd, p.prod_air_end_ymd, p.prod_curr_stcd, p.prod_ntsl_bgn_ymd, p.prod_ntsl_end_ymd FROM tb_product p LEFT JOIN tb_code c1 ON c1.code_cl = p.prod_ty_cd_code_cl AND c1.code_no = p.prod_ty_cd LEFT JOIN tb_code c2 ON c2.code_cl = p.prod_sbstg_ty_cd_code_cl AND c2.code_no = p.prod_sbstg_ty_cd LEFT JOIN tb_code c3 ON c3.code_cl = p.prod_pay_ty_cd_code_cl AND c3.code_no = p.prod_pay_ty_cd LEFT JOIN tb_code c4 ON c4.code_cl = p.prod_int_tax_ty_cd_code_cl AND c4.code_no = p.prod_int_tax_ty_cd WHERE 1=1
AND p.prod_ty_cd = #{prodTyCd}
AND p.prod_curr_stcd = #{prodCurrStcd}
AND p.prod_pay_ty_cd = #{prodPayTyCd}
AND p.prod_nm LIKE CONCAT('%', #{prodNm}, '%')
AND p.prod_ntsl_bgn_ymd BETWEEN #{prodNtslBgnYmdRange[0]} AND #{prodNtslBgnYmdRange[1]}
AND p.prod_ntsl_end_ymd BETWEEN #{prodNtslEndYmdRange[0]} AND #{prodNtslEndYmdRange[1]}
